import React, { useCallback, useEffect, useState } from "react";
import "./reset.css";
import "./login.scss";
import Footer from "./Footer";
import { useNavigate } from "react-router-dom";
import { connect, useDispatch } from "react-redux";
import { setGeneration } from ".";

function Login(props) {
  const navigate = useNavigate();
  const [nickName, setNickName] = useState("");
  const dispatch = useDispatch();
  const userData = props.currentUser;

  function signUp(e) {
    e.preventDefault();
    sendNewUser(nickName);
    localStorage.setItem(USER_ID, nickName);
  }

  const sendNewUser = () => {
    const thisDay = new Date();
    const findyear = thisDay.getFullYear();
    const findmonth = thisDay.getMonth();
    const findDay = thisDay.getDate();
    let saveDay = {
      findyear: findyear,
      findmonth: findmonth,
      findDay: findDay,
    };
    dispatch(setGeneration(saveDay));
    navigate("/");
  };

  const updateNickName = useCallback(
    (e) => {
      setNickName(e.target.value);
    },
    [nickName]
  );

  return (
    <div className="center_wrap">
      <div className="gretting_wrap">
        <h4 className="js-grettings grettings">{nickName}</h4>
      </div>
      <form className="js-form form" onSubmit={signUp}>
        <input
          type="text"
          placeholder="사용 할 닉네임을 적어주세요."
          onChange={updateNickName}
        />
        <p className="resign">(재지정하는 경우 1일째로 다시시작합니다)</p>
      </form>
      <Footer></Footer>
    </div>
  );
}

export default Login;
