                                  );
                                  props.dispatch({
                                    type: "날짜전송",
                                    payload2: DateFac,
                                  });
                                  props.dispatch({ type: "알림보기" });
                                  props.dispatch({
                                    type: "텍스트보내기",
                                    payload3: parsedTodos[i].write,
