import React from "react";
import { Link } from "react-router-dom";
import { connect, useSelector } from "react-redux";
import Clock from "./clock";
function Header(props) {
  const location = window.location.pathname;
  const issueBell = useSelector((state) => state.issue);

  return (
    <header className="main_header">
      <Clock />
      {location === "/" ? (
        <Link to="/Mypage" className="main_nav">
          <img src="./img/nav.svg" alt="nav"></img>
        </Link>
      ) : (
        <Link to="/Mypage" className="header_nav">
          <img src="./img/locate_arrow.svg" alt="nav"></img>
        </Link>
      )}
      <div className="title_header">
        <p className="main_titles">
          <Link to="/">완벽한 하루</Link>
        </p>
        {props.state3[0].Nobell === true ? (
          <img
            src="/img/bell.svg"
            alt="bell"
            onClick={() => {
              props.dispatch({ type: "알림열기" });
            }}
          ></img>
        ) : (
          <img src="/img/no_bell.svg" alt="bell" className="down"></img>
        )}
      </div>
    </header>
  );
}

export default Header;
