import React, { useState } from "react";
import MainFooter from "../components/main_footer";
function List() {
  let [addToggle, setAddToggle] = useState(false);
  let [reset, setreset] = useState(false);
  return (
    <>
      <section className="section02">
        <div className="in_s2">
          <div className="schedule">
            <p>일정스케줄</p>
            {reset === false ? (
              <p
                className="reset"
                onClick={() => {
                  setAddToggle(true);
                  setreset(true);
                }}
              >
                작성완료
                <img src="/img/plus.svg" alt="더보기"></img>
              </p>
            ) : (
              <p
                className="reset"
                onClick={() => {
                  localStorage.removeItem("data");
                  setreset(false);
                  setAddToggle(false);
                  localStorage.removeItem("parcent");
                  window.location.reload();
                }}
              >
                초기화하기
              </p>
            )}
          </div>

          <div className="list">
            <p className="today_date">123</p>
            <p className="today_txt">123</p>
            <button>
              <img src="/img/before_check.svg" alt="check"></img>
            </button>
          </div>
        </div>
      </section>
      <MainFooter addToggle={addToggle}></MainFooter>
    </>
  );
}

export default List;
