import React, { useState, useEffect } from "react";
import Header from "./header";
import List from "./List";
function Home(props) {
  const [parcent, setparcent] = useState(0);
  const [plusDay, setplusDay] = useState(1);
  const UserName = props.currentUser;
  const loadCreator = JSON.parse(localStorage.getItem(props.creation));
  console.log(loadCreator);

  useEffect(() => {
    const countDownDate = new Date();
    countDownDate.setHours(24, 0, 0, 0);

    const timer = setInterval(function () {
      // 현재 날짜와 시간
      const now = new Date().getTime();
      // 현재 시각과 카운트다운 시각과의 차이
      const distance = countDownDate - now;

      // 시간, 분, 초 계산
      if (distance === 0) {
        setparcent(0);
        localStorage.removeItem("data");
        localStorage.removeItem("parcent");
      }
    }, 1000);

    setInterval(() => {
      const now = new Date();

      let start = new Date();
    }, 86400);
    //24시간
  }, []);

  return (
    <div className="homeRoot">
      <div className="wrap">
        <Header />
        <section className="section01">
          <div className="s1_wrap">
            <div className="s1_txt_wrap">
              <p className="today">오늘 하루</p>
              <p className="parcent">
                {parcent > 100 ? setparcent(100) : parcent}%
              </p>
              <p className="caption">완벽한 하루를 보내셨습니다!</p>
            </div>

            {parcent === 0 ? (
              <img src="/img/wow.svg" alt="준비"></img>
            ) : parcent === 25 ? (
              <img src="/img/ready.svg" alt="하는중" className="ing"></img>
            ) : parcent === 50 ? (
              <img src="/img/50.svg" alt="하는중" className="wow1"></img>
            ) : parcent === 75 ? (
              <img src="/img/75.svg" alt="하는중" className="wow2"></img>
            ) : parcent === 100 ? (
              <img src="/img/ing.svg" alt="하는중" className="wow3"></img>
            ) : (
              <img src="/img/ing.svg" alt="하는중" className="wow4"></img>
            )}
          </div>

          <div className="race">
            <p className="member">{UserName}</p>
            <div className="member_caption">
              님은 {plusDay}일째 완벽한 하루를 사용중!!
            </div>
          </div>
        </section>
        <List />
      </div>
    </div>
  );
}

export default Home;
