import React, { useCallback, useEffect, useState } from "react";
import Header from "./header";
import MainFooter from "./mainFooter";
import { ArrowBackIos, ArrowForwardIos } from "@material-ui/icons";
import "../reset.css";
import "../asset/calendar.scss";
import { useDispatch, useSelector } from "react-redux";
import { monMinutesAction, monPlusAction } from "..";

function Calendar() {
  const date = new Date();
  const utc = date.getTime() + date.getTimezoneOffset() * 60 * 1000;
  const kstGap = 9 * 60 * 60 * 1000;
  let today = new Date(utc + kstGap);
  let arr,
    arr1,
    arr2 = [];
  let monData = useSelector((state) => state.monData);
  const dispatch = useDispatch();
  let thisMonth = new Date(
    today.getFullYear(),
    today.getMonth(),
    today.getDate()
  );

  let currentYear = thisMonth.getFullYear();
  let currentMonth = thisMonth.getMonth();
  let currentDate = thisMonth.getDate();

  function timeLogic(params) {
    currentYear = params.getFullYear();
    currentMonth = params.getMonth();
    currentDate = params.getDate();

    let startDay = new Date(currentYear, currentMonth, 0);
    let prevDate = startDay.getDate();
    let prevDay = startDay.getDay();

    // 이번 달의 마지막날 날짜와 요일 구하기
    let endDay = new Date(currentYear, currentMonth + 1, 0);
    let nextDate = endDay.getDate();
    let nextDay = endDay.getDay();

    prevMonth(prevDate, prevDay).then((result) => {
      setPrevDate(result);
    });
    nowMonth(nextDate).then((result) => {
      setThisMons(result);
    });
    nextMonth(thisMonWeek).then((result) => {
      setNextMon(result);
    });

    const prevMonth = (prevDate, prevDay) => {
      return new Promise(function (res) {
        arr = [];
        for (var i = prevDate - prevDay + 1; i <= prevDate; i++) {
          arr.push(i);
        }
      });
    };

    const nowMonth = (mon) => {
      return new Promise(function (res) {
        arr = [];
        for (var i = 1; i <= mon; i++) {
          arr.push(i);
          res(arr);
        }
      });
    };

    const nextMonth = (next) => {
      return new Promise(function (res) {
        arr = [];
        for (var i = 1; i <= (7 - next == 7 ? 0 : 7 - next); i++) {
          arr.push(i);
          res(arr);
        }
      });
    };
  }

  useEffect(() => {
    timeLogic(thisMonth);
  }, []);

  //오늘 날짜 표시해주는 함수

  return (
    <div className="wrap calendar">
      <Header />
      <section className="cal_data">
        <div className="title_wrap">
          <div className="date_top">
            <div className="date_title">예약된 일정</div>
            <div className="date_title">
              <ArrowBackIos
                color="action"
                className="prev_btn"
                // onClick={(e) => changeMonth(e)}
              />
              {/* {thisMonth.getMonth() + 1 + monData}월 */}
              <ArrowForwardIos
                color="action"
                className="next_btn"
                // onClick={(e) => changeMonth(e)}
              />
            </div>
          </div>
          <div className="cal_wrap">
            <div className="days">
              <div className="day">MON</div>
              <div className="day">TUE</div>
              <div className="day">WED</div>
              <div className="day">THU</div>
              <div className="day">FRI</div>
              <div className="day">SAT</div>
              <div className="day">SUN</div>
            </div>
            <div className="date_wrap">
              {/* {concatArray.map((value, index) => {
                return <div className="day dateDay">{value}</div>;
              })} */}
            </div>
          </div>
        </div>
      </section>
      <MainFooter />
    </div>
  );
}

export default Calendar;
