import React from "react";
import Header from "./header";
import MainFooter from "./mainFooter";
import DayInput from "../components/day";

import "../reset.css";
import "../asset/calendar.scss";

function Calendar(props) {
  const date = new Date();
  const utc = date.getTime() + date.getTimezoneOffset() * 60 * 1000;
  const kstGap = 9 * 60 * 60 * 1000;
  const today = new Date(utc + kstGap);

  const thisMonth = new Date(
    today.getFullYear(),
    today.getMonth(),
    today.getDate()
  );

  const currentYear = thisMonth.getFullYear();
  const currentMonth = thisMonth.getMonth();
  const currentDate = thisMonth.getDate();

  const startDay = new Date(currentYear, currentMonth, 0);
  const prevDate = startDay.getDate();
  const prevDay = startDay.getDay();

  const endDay = new Date(currentYear, currentMonth + 1, 0);
  const nextDate = endDay.getDate();
  const nextDay = endDay.getDay();

  const parentDay = document.querySelector(".date_wrap");
  const dayCreate = document.createElement("div");
  dayCreate.classList.add("day");

  for (var i = prevDate - prevDay + 1; i <= prevDate; i++) {
    parentDay.appendChild(dayCreate);
  }

  return (
    <div className="wrap calendar">
      <Header />
      <p className="todays">TODAY</p>
      <hr />
      <section className="cal_data">
        <div className="title_wrap">
          <div className="date_title">예약된 일정</div>
          <div class="cal_wrap">
            <div class="days">
              <div class="day">MON</div>
              <div class="day">TUE</div>
              <div class="day">WED</div>
              <div class="day">THU</div>
              <div class="day">FRI</div>
              <div class="day">SAT</div>
              <div class="day">SUN</div>
            </div>
            <div class="date_wrap"></div>
          </div>
        </div>
      </section>
      <MainFooter />
    </div>
  );
}

export default Calendar;
