import React from "react";
import ReactDOM from "react-dom";
import "./index.css";
import App from "./App";
import reportWebVitals from "./reportWebVitals";
import { BrowserRouter } from "react-router-dom";
import { Provider } from "react-redux";
import { createStore, applyMiddleware } from "redux";
import logger from "redux-logger";

const initialState = {
  editorToggle: false,
  Todo: [],
  successDate: [],
  parcent: 0,
  loadding: false,
};

const ToggleEditor = "ToggleEditor";
const Todo = "Todo";
const success = "success";
const parcent = "parcent";
const clear = "clear";
const loadList = "loadList";

export const EditorAction = () => ({
  type: ToggleEditor,
});

export const TodoPost = (data) => ({
  type: Todo,
  data,
});

export const successDate = (date) => ({
  type: success,
  date,
});

export const ParcentAction = (data) => ({
  type: parcent,
  data,
});

export const clearSaveList = () => ({
  type: clear,
});

export const LoadSaveList = (data) => ({
  type: loadList,
  data,
});

const store = createStore(reducer, applyMiddleware(logger));

function reducer(state = initialState, action) {
  switch (action.type) {
    case ToggleEditor:
      return {
        ...state,
        editorToggle: !state.editorToggle,
      };
    case Todo:
      return {
        ...state,
        Todo: [...state.Todo, action.data],
        editorToggle: !state.editorToggle,
      };
    case success:
      return {
        ...state,
        successDate: [...state.successDate, action.date],
      };
    case parcent:
      return {
        ...state,
        parcent: state.parcent + 25,
      };
    case clear:
      return {
        ...state,
        Todo: [...state.Todo],
      };
    case loadList:
      return {
        ...state,
        loadding: (state.loadding = true),
        Todo: [...state.Todo, action.data],
      };
    default:
      return state;
  }
}

ReactDOM.render(
  <React.StrictMode>
    <BrowserRouter>
      <Provider store={store}>
        <App />
      </Provider>
    </BrowserRouter>
  </React.StrictMode>,
  document.getElementById("root")
);

// If you want to start measuring performance in your app, pass a function
