import React, { useCallback, useEffect, useState } from "react";
import Header from "./header";
import MainFooter from "./mainFooter";

import "../reset.css";
import "../asset/calendar.scss";

function Calendar() {
  const [prevDate, setPrevDate] = useState([]);
  const [thisMons, setThisMons] = useState([]);
  const [nextMon, setNextMon] = useState([]);
  const [concatArray, setConcat] = useState([]);
  const [monthCheck, setMonthCheck] = useState(false);
  let [todayLine, setTodayLine] = useState();
  const date = new Date();
  const utc = date.getTime() + date.getTimezoneOffset() * 60 * 1000;
  const kstGap = 9 * 60 * 60 * 1000;
  let arr = [];
  const today = new Date(utc + kstGap);
  // 여기까지 한국시간

  const reactToday = new Promise(function (res) {
    const today = new Date(utc + kstGap);
    res(today);
  });

  reactToday.then((result) => {
    setTodayLine(result);
  });

  let thisMonth = new Date(
    today.getFullYear(),
    today.getMonth(),
    today.getDate()
  );
  let currentYear = thisMonth.getFullYear();
  let currentMonth = thisMonth.getMonth();
  let currentDate = thisMonth.getDate();

  function TimeDate() {
    const prevEndDay = new Date(currentYear, currentMonth, 0);
    //지정달에서 마지막에 0을 붙여지면 저번달 마지막날이 출력됨

    const prevDay = prevEndDay.getDate();
    const prevWeek = prevEndDay.getDay();
    //주어진 날짜의 요일을 반환 0 : 일요일 , 1 : 월요일 등등
    // 즉 31일 화요일을 출력

    const thisMon = new Date(currentYear, currentMonth + 1, 0);
    const thisMonDay = thisMon.getDate();
    const thisMonWeek = thisMon.getDay();
    //month는 -1 이랬으니 + 1 해줘야 이번달의 마지막날이 출력
    // 이번달 마지막날

    prevMonth(prevDay, prevWeek).then((result) => {
      setPrevDate(result);
    });
    nowMonth(thisMonDay).then((result) => {
      setThisMons(result);
    });
    nextMonth(thisMonWeek).then((result) => {
      setNextMon(result);
    });
  }

  const prevMonth = (day, week) => {
    return new Promise(function (res) {
      arr = [];
      for (var i = day - week + 1; i <= day; i++) {
        arr.push(i);
        res(arr);
      }
    });
  };

  const nowMonth = (mon) => {
    return new Promise(function (res) {
      arr = [];
      for (var i = 1; i <= mon; i++) {
        arr.push(i);
        res(arr);
      }
    });
  };

  const nextMonth = (next) => {
    return new Promise(function (res) {
      arr = [];
      for (var i = 1; i <= (7 - next == 7 ? 0 : 7 - next); i++) {
        arr.push(i);
        res(arr);
      }
    });
  };

  function changeMonth(e) {
    if (e.target.classList.contains("prev_btn")) {
      thisMonth = new Date(currentYear, currentMonth - 1, 1);
      console.log(thisMonth);
      TimeDate(thisMonth);
      setMonthCheck(true);
    } else {
      thisMonth = new Date(currentYear, currentMonth + 1, 1);
      TimeDate(thisMonth);
      setMonthCheck(true);
    }
  }

  useEffect(() => {
    TimeDate();
  }, []);

  useEffect(() => {
    console.log(todayLine);
  }, [todayLine]);

  useEffect(() => {
    if (nextMon.length != 0) {
      if (prevDate.length != 0 && thisMons.length != 0 && nextMon.length != 0) {
        setConcat([...prevDate, ...thisMons, ...nextMon]);
      }
    }
  }, [nextMon]);

  useEffect(() => {
    if (concatArray.length != 0) {
      if (today.getMonth() == currentMonth) {
        let todayDate = today.getDate();
        let currentMonthDate = Array.from(
          document.querySelectorAll(".dateDay")
        );
        currentMonthDate[todayDate + prevDate.length - 1].classList.add(
          "today"
        );
      }
      setMonthCheck(false);
    }
  }, [concatArray]);

  useEffect(() => {
    if (monthCheck === true) {
      document.querySelector(".today").classList.remove("today");
    }
  }, [monthCheck]);

  return (
    <div className="wrap calendar">
      <Header />
      <p className="todays">TODAY</p>
      <hr />
      <section className="cal_data">
        <div className="title_wrap">
          <div className="date_top">
            <div className="date_title">예약된 일정</div>
            <div className="date_title">
              <img
                className="prev_btn"
                onClick={(e) => changeMonth(e)}
                src="./img/locate_arrow.svg"
                alt="nav"
                style={{ width: 10 }}
              />
              {currentMonth + 1}월
              <img
                className="next_btn"
                onClick={(e) => changeMonth(e)}
                src="./img/locate_arrow.svg"
                alt="nav"
                style={{ width: 10, transform: "scaleX(-1)" }}
              />
            </div>
          </div>
          <div className="cal_wrap">
            <div className="days">
              <div className="day">MON</div>
              <div className="day">TUE</div>
              <div className="day">WED</div>
              <div className="day">THU</div>
              <div className="day">FRI</div>
              <div className="day">SAT</div>
              <div className="day">SUN</div>
            </div>
            <div className="date_wrap">
              {concatArray.map((value, index) => {
                return <div className="day dateDay">{value}</div>;
              })}
            </div>
          </div>
        </div>
      </section>
      <MainFooter />
    </div>
  );
}

export default Calendar;
