import React, { useEffect } from "react";
import "./reset.css";
import "./login.scss";
import Footer from "./Footer";
import { useNavigate } from "react-router-dom";
import { connect } from "react-redux";

function Login() {
  const form = document.getElementsByClassName("js-form")[0],
    input = form.getElementsByTagName("input"),
    gretting = document.getElementsByClassName("js-grettings")[0],
    log = document.getElementsByClassName("gretting_wrap")[0],
    out = log.getElementsByClassName("out")[0];

  const USER_ID = "currentUser";

  useEffect(() => {
    function loadName() {
      let currentUser = localStorage.getItem(USER_ID);
      if (currentUser === null) {
        signUp();
      } else {
        loginRequest();
      }
    }
  }, []);

  function signUp(e) {
    e.preventDefault();
  }

  function loginRequest() {}

  return (
    <div className="center_wrap">
      <div className="gretting_wrap">
        <h4 class="js-grettings grettings"> </h4>
      </div>
      <form class="js-form form" onSubmit={signUp}>
        <input type="text" placeholder="사용 할 닉네임을 적어주세요." />
        <p className="resign">(재지정하는 경우 1일째로 다시시작합니다)</p>
      </form>
      <Footer></Footer>
    </div>
  );
}

export default Login;
