import React from "react";

function List() {
  let [addToggle, setAddToggle] = useState(false);
  let [reset, setreset] = useState(false);
  return (
    <section className="section02">
      <div className="in_s2">
        <div className="schedule">
          <p>일정스케줄</p>
          {resets === false ? (
            <p
              className="reset"
              onClick={() => {
                setAddToggle(true);
                setresets(true);
              }}
            >
              작성완료
              <img src="/img/plus.svg" alt="더보기"></img>
            </p>
          ) : (
            <p
              className="reset"
              onClick={() => {
                localStorage.removeItem("data");
                setresets(false);
                setAddToggle(false);
                localStorage.removeItem("parcent");
                window.location.reload();
              }}
            >
              초기화하기
            </p>
          )}
        </div>
      </div>
    </section>
  );
}

export default List;
