import React, { useState, useEffect, useCallback } from "react";
import Header from "./header";
import List from "./List";
import Editor from "./editor";
import { useDispatch, useSelector } from "react-redux";
import { LoadSaveList } from "..";
import Notification from "./Notification";
function Home(props) {
  const [plusDay, setplusDay] = useState(1);
  const [parcent, setPercent] = useState(0);
  const loaddingState = useSelector((state) => state.loadding);
  const saveList = "saveList";

  const counter = "clearCounter";
  const UserName = props.currentUser;
  const loadParcent = 0;
  const issueState = useSelector((state) => state.issue);
  const dispatch = useDispatch();

  useEffect(() => {
    const loadList = new Promise(function (res, rej) {
      const result = JSON.parse(localStorage.getItem(saveList));
      res(result);
    });

    loadList.then((result) => {
      if (
        typeof result === "object" &&
        result != null &&
        loaddingState === false
      ) {
        let arr = [...result];
        dispatch(LoadSaveList(arr));
      }
    });

    dayMemo();

    const loadCounter = new Promise(function (res, rej) {
      const result = JSON.parse(localStorage.getItem());
    });
  }, []);

  const dayMemo = useCallback(async () => {
    const loadCreator = JSON.parse(localStorage.getItem(props.creation));
    const now = new Date();

    let start = await new Date(
      `${loadCreator.findyear},${loadCreator.findmonth + 1},
      ${loadCreator.findDay - 1}`
    );

    const diff = now - start;

    const Today = 1000 * 60 * 60 * 24;
    setplusDay(Math.floor(diff / Today));
  }, [plusDay]);

  return (
    <div className="wrap">
      <Header />
      <section className="section01">
        <div className="s1_wrap">
          <div className="s1_txt_wrap">
            <p className="today">오늘 하루</p>
            <p className="parcent">{loadParcent}%</p>
            <p className="caption">완벽한 하루를 보내셨습니다!</p>
          </div>

          {loadParcent === 0 ? (
            <img src="/img/wow.svg" alt="준비"></img>
          ) : loadParcent === 25 ? (
            <img src="/img/ready.svg" alt="하는중" className="ing"></img>
          ) : loadParcent === 50 ? (
            <img src="/img/50.svg" alt="하는중" className="wow1"></img>
          ) : loadParcent === 75 ? (
            <img src="/img/75.svg" alt="하는중" className="wow2"></img>
          ) : loadParcent === 100 ? (
            <img src="/img/ing.svg" alt="하는중" className="wow3"></img>
          ) : (
            <img src="/img/ing.svg" alt="하는중" className="wow4"></img>
          )}
        </div>

        <div className="race">
          <p className="member">{UserName}</p>
          <div className="member_caption">
            님은 {plusDay}일째 완벽한 하루를 사용중!!
          </div>
        </div>
      </section>
      <List loadParcent={loadParcent} saveList={saveList} />
      <Editor />
      {issueState ? <Notification /> : null}
    </div>
  );
}

export default Home;
