import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { successDate } from "../index";
import MainFooter from "../components/main_footer";
function List() {
  const todolist = useSelector((state) => state.Todo);
  const saveList = "saveList";
  const dispatch = useDispatch();

  const nowTime = new Date(),
    year = nowTime.getFullYear(),
    month = nowTime.getMonth() + 1,
    date = nowTime.getDate(),
    hour = nowTime.getHours(),
    min = nowTime.getMinutes();

  const DateFac = {
    year: year,
    month: month,
    date: date,
    hour: hour,
    min: min,
  };

  function saveHandler() {
    window.alert("현재까지의 리스트를 저장합니다");
    localStorage.setItem(saveList, JSON.stringify(todolist));
  }

  function successHandler() {
    dispatch(successDate(DateFac));

    const finded = document.querySelectorAll(".section02 .list button"),
      todayFind = document.querySelectorAll(".section02 .list .today_date"),
      todayFind2 = document.querySelectorAll(".section02 .list .today_txt");
  }

  return (
    <>
      <section className="section02">
        <div className="in_s2">
          <div className="schedule">
            <p>일정스케줄</p>
            <span onClick={saveHandler}>저장</span>
          </div>
          {todolist.map((value, index) => {
            return (
              <>
                <div className="list" key={index}>
                  <p className="today_date">
                    {value.writeH}:{value.writeM}
                  </p>
                  <p className="today_txt">{value.write}</p>
                  <button onClick={successHandler}>
                    <img src="/img/before_check.svg" alt="check"></img>
                  </button>
                </div>
              </>
            );
          })}
        </div>
      </section>
      <MainFooter></MainFooter>
    </>
  );
}

export default List;
