import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { issueAction } from "..";
import "../asset/notification.scss";

function Notification() {
  const finishData = useSelector((state) => state.successDate[0]);
  const finishTitle = useSelector((state) => state.successCon);
  const [emptyCheck, setEmpty] = useState(false);
  const dispatch = useDispatch();
  return (
    <div className="noti_wrap">
      <div className="cover"></div>
      <aside>
        <div className="aside_header">
          <p className="noti_title">알림함</p>
          <img
            src="/img/clear.svg"
            onClick={() => dispatch(issueAction())}
          ></img>
        </div>
        <ul className="noti_body">
          <li>
            <img src="/img/check_btn.svg"></img>
            <div className="li_txt_wrap">
              <p className="li_time">
                {finishData === 0
                  ? ""
                  : `${finishData.year}년 ${finishData.month}월 ${finishData.date}일 ${finishData.hour}:${finishData.min}`}
              </p>
              <p className="li_title">{finishTitle === 0 ? "" : finishTitle}</p>
            </div>
          </li>
        </ul>
      </aside>
    </div>
  );
}

export default Notification;
