import React, { useEffect, useState } from "react";
import { batch, useDispatch, useSelector } from "react-redux";
import {
  clearSaveList,
  issueAction,
  ParcentAction,
  reportAction,
  successDate,
} from "../index";
import MainFooter from "../components/main_footer";
function List(props) {
  const todolist = useSelector((state) => state.Todo);
  const dispatch = useDispatch();

  function createPost() {
    const nowTime = new Date(),
      year = nowTime.getFullYear(),
      month = nowTime.getMonth() + 1,
      date = nowTime.getDate(),
      hour = nowTime.getHours(),
      min = nowTime.getMinutes();

    const DateFac = {
      year: year,
      month: month,
      date: date,
      hour: hour,
      min: min,
    };
    return DateFac;
  }

  function saveHandler() {
    window.alert("현재까지의 리스트를 저장합니다");
    localStorage.setItem(props.saveList, JSON.stringify(todolist));
  }

  function successHandler(e) {
    batch(() => {
      dispatch(successDate(createPost()));
      if (props.loadParcent < 100) {
        dispatch(ParcentAction(parseInt(25)));
      }
      dispatch(reportAction());
    });
    Array.from(e.currentTarget.parentElement.getElementsByTagName("p")).forEach(
      (item) => {
        item.classList.add("op_off");
      }
    );
    // e.currentTarget.classList.add("off");
  }

  function deleteHandler() {
    localStorage.removeItem(props.saveList);
    dispatch(clearSaveList());
  }

  return (
    <>
      <section className="section02">
        <div className="in_s2">
          <div className="schedule">
            <p>일정스케줄</p>
            <div>
              <span onClick={saveHandler}>저장</span>
              <span onClick={deleteHandler}>초기화</span>
            </div>
          </div>
        </div>
      </section>
      <MainFooter></MainFooter>
    </>
  );
}

export default List;
