import React, { useEffect, useState } from "react";
import { batch, useDispatch, useSelector } from "react-redux";
import { contentAction, reportAction, successDate } from "../index";
import MainFooter from "../components/main_footer";
import styled from "styled-components";

const ClearList = styled.p`
  color: #ddd;
  font-family: "AppleSDGothicNeoM";
  text-decoration: line-through;
`;

const ClearBtn = styled.button`
  pointer-events: none;
  opacity: 0;
`;

function List(props) {
  const todolist = useSelector((state) => state.Todo);
  const [listState, setListState] = useState("");
  const dispatch = useDispatch();
  const loadList = localStorage.getItem(props.saveList);

  useEffect(() => {}, []);

  function createPost() {
    const nowTime = new Date(),
      year = nowTime.getFullYear(),
      month = nowTime.getMonth() + 1,
      date = nowTime.getDate(),
      hour = nowTime.getHours(),
      min = nowTime.getMinutes();

    const DateFac = {
      year: year,
      month: month,
      date: date,
      hour: hour,
      min: min,
    };
    return DateFac;
  }

  function saveHandler() {
    window.alert("현재까지의 리스트를 저장합니다");
    localStorage.setItem(props.saveList, JSON.stringify(listState));
  }

  function successHandler(e) {
    const successTitle =
      e.currentTarget.parentElement.getElementsByClassName("today_txt")[0]
        .innerHTML;
    batch(() => {
      dispatch(successDate(createPost()));
      dispatch(reportAction());
      dispatch(contentAction(successTitle));
    });
  }

  function deleteHandler() {
    localStorage.removeItem(props.saveList);
    window.location.reload();
  }

  return (
    <>
      <section className="section02">
        <div className="in_s2">
          <div className="schedule">
            <p>일정스케줄</p>
            <div>
              <span onClick={saveHandler}>저장</span>
              <span onClick={deleteHandler}>초기화</span>
            </div>
          </div>
          {listState
            .filter((value, idx, arr) => {
              return (
                arr.findIndex(
                  (item) =>
                    item.write === value.write &&
                    item.writeH === value.writeH &&
                    item.writeM === value.writeM
                ) === idx
              );
            })
            .map((listData, index) => {
              return (
                <div className="list" key={index}>
                  {listState[index].clear === false ? (
                    <p className="today_date">
                      {listData.writeH}:{listData.writeM}
                    </p>
                  ) : (
                    <ClearList>
                      {listData.writeH}:{listData.writeM}
                    </ClearList>
                  )}

                  {listState[index].clear === false ? (
                    <p className="today_txt">{listData.write}</p>
                  ) : (
                    <ClearList>{listData.write}</ClearList>
                  )}
                  {listState[index].clear === false ? (
                    <button
                      onClick={(e) => {
                        if (loadList != null) {
                          const clearCheck = [...listState];
                          clearCheck[index].clear = true;
                          localStorage.setItem(
                            props.saveList,
                            JSON.stringify(clearCheck)
                          );
                        }
                        successHandler(e);
                      }}
                    >
                      <img src="/img/before_check.svg" alt="check"></img>
                    </button>
                  ) : (
                    <ClearBtn />
                  )}
                </div>
              );
            })}
        </div>
      </section>
      <MainFooter></MainFooter>
    </>
  );
}

export default List;
