import React from "react";
import { Link } from "react-router-dom";
import { batch, connect, useDispatch, useSelector } from "react-redux";
import Clock from "./clock";
import { issueAction, reportAction } from "..";
function Header() {
  const location = window.location.pathname;
  const issueBell = useSelector((state) => state.report);
  const dispatch = useDispatch();
  return (
    <header className="main_header">
      <Clock />
      {location === "/" ? (
        <Link to="/Mypage" className="main_nav">
          <img src="./img/nav.svg" alt="nav"></img>
        </Link>
      ) : (
        <Link to="/Mypage" className="header_nav">
          <img src="./img/locate_arrow.svg" alt="nav"></img>
        </Link>
      )}
      <div className="title_header">
        <p className="main_titles">완벽한 하루</p>
        {issueBell ? (
          <img
            src="/img/bell.svg"
            alt="bell"
            onClick={() => dispatch(issueAction())}
          ></img>
        ) : (
          <img
            src="/img/no_bell.svg"
            alt="bell"
            className="down"
            onClick={() => dispatch(issueAction())}
          ></img>
        )}
      </div>
    </header>
  );
}

export default Header;
